css:
/* Override core properties */
h1 {
    margin: 0;
    font-weight: bold;
    font-size: 30pt;
}

h4, h5 {
    margin: 0 0;
}

label {
    margin-bottom: 4px;
    font-size: 8pt;
}

/* Override default themes */

.print-heading {
    padding-bottom: 0;
    margin-bottom: 0;
    border-bottom: 0;
    text-transform: none;
    font-size: 8pt; /* 9pt is the default */
}

.section-break {
    border-bottom: solid thin black;
}


/* Custom Properties */
.border-left {
    border-left: solid thin black;
}

.padding-left {
    padding-left: 5px;
}
